import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';

@Component({
  selector: 'app-welcome',
  templateUrl: './welcome.component.html',
  styleUrls: ['./welcome.component.css'],
})
export class WelcomeComponent implements OnInit {
  // Name input from the user.
  @ViewChild('name') nameKey!: ElementRef;
  constructor() {}

  ngOnInit(): void {}
  startTests() {
    localStorage.setItem('name', this.nameKey.nativeElement.value);
  }
}
<div class="container bg-light py-5">
  <h1 class="display-5 fw-bold">Welcome to the [Online Test App]</h1>
  <p class="col-md-8 fs-4">The test will contain a total of 5 questions. Each Question scores 5 marks.</p>
  <h4>Instructions:</h4>
  <ul>
    <li>Correct answer gives you 5 marks.</li>
    <li>Incorrect answer gives to -5 marks.</li>
    <li>There will be 60 sec to answer each question.</li>
    <li>Any page referesh will reset the answers.</li>
  </ul>
  <h1 style="font-family: cursive;text-align:center">Best of luck in your test<span>&#9786;</span></h1>

  <div class="row g-0 text-center">
    <div class="col-sm-6 col-md-8">
      <input #name type="text" class="form-control" placeholder="Enter your name here please" >
    </div>

  </div>
  <br>
  <div class="text-center">
    <button class="btn btn-lg" routerLink="/quiz" (click)="startTests()">[Click to begin]</button>
  </div>
</div>
